rovider).html('<span style="color:Red!important"><strong>' + $('#tdVAT_' + idProvider).text() + '</strong></span>');                },                'text'            );        }        else { toastr.error('Saisie abandonnée ou incorrecte.', 'Échec.'); }    }        // Change an Enterprise (provider) typeof LIVE    function changeTypeOf($idEnterprise) {        var newType = prompt("Chosir le type de commission (T = 1, C = 2) :");        if(newType != null && newType.length > 0) { // Check before update                        newType = newType.trim().toUpperCase(); // The trim() method removes whitespace from both sides of a string // UpperCase = MAJ                        if(newType === 'C' || newType === 'T') {                $.post(                    './ajax/rename_typeof_provider.php',                    {                        $idEnterprise : $idEnterprise,                        newType : newType                    },                    function(data) {                        data = data.trim();                        if(data !== '-1')                            $('#tdTypeOfProvider_' + $idEnterprise).html('<span style="color:Green!important"><strong>' + data + '</strong></span>');                        else                            $('#tdTypeOfProvider_' + $idEnterprise).html('<span style="color:Red!important"><strong>' + $('#tdTypeOfProvider_' + $idEnterprise).text() + '</strong></span>');                    },                    'text'                );            }            else { toastr.error('Saisie incorrecte.', 'Échec.'); }        }        else { toastr.error('Saisie abandonnée.', 'Échec.'); }    }        var checkbox_3 = document.querySelector('.js-switch_3');    var switchery_3 = new Switchery(checkbox_3, { color: '#1AB394', secondaryColor: '#ED5565' });    checkbox_3.onchange = function() {        // console.log(checkbox_3.checked);        switch (checkb