                 customize: function (win){                        $(win.document.body).addClass('white-bg');                        $(win.document.body).css('font-size', '10px');                        $(win.document.body).find('table')                            .addClass('compact')                            .css('font-size', 'inherit');                    }                }            ],            initComplete: function( settings, json ) {                $('#myspinner').hide();                $('#divTableStores').show();            }        });    });        // Rename a Store LIVE    function renameStore(idStore) {        var oldName = $('#tdNameStore_' + idStore).text();        var newName = prompt("Choisir le nouveau nom de Fournisseur :", oldName); // Get the previous value of file description        if(newName != null && newName.length > 1 && newName != oldName) { // Check before update                        newName = newName.trim().toUpperCase(); // The trim() method removes whitespace from both sides of a string            $.post(                './ajax/rename_store.php',                {                    idStore : idStore,                    newName: newName                },                function(data) {                    if(data.trim() == '1')                        $('#tdNameStore_' + idStore).html('<span style="color:Green!important"><strong>' + newName + '</strong></span>');                        // location.reload();                    else                        $('#tdNameStore_' + idStore).html('<span style="color:Red!important"><strong>' + $('#tdNameStore_' + idStore).text() + '</strong></span>');                },                'text'            );        }        else { toastr.error('Saisie abandonnée ou incorrecte.', 'Échec.'); }    }        // Rename a Panel Store LIVE    function renamePanelStore(idStore) {        var oldPanel = $('#tdPanelStore_' + idStore).text();        var newPanel = prompt("Choisir le nouveau code fournisseur :", oldPanel); // Get the previous value of file description        if(newPanel != null && newPanel.length == 5 && newPanel != oldPanel) { // Check before update                        newPanel = newPanel.trim().toUpperCase(); // The trim() method removes whitespace from both sides of a string            $.post(                './ajax/rename_panel_store.php',                {                    idStore  : idStore,                    newPanel : newPanel                },                function(data) {                    if(data.trim() == '1')                        $('#tdPanelStore_' + idStore).html('<span style="color:Green!important"><strong>' + newPanel + '</strong></span>');                        // location.reload();                    else                        $('#tdPanelStore_' + idStore).html('<span style="color:Red!important"><strong>' + $('#tdPanelStore_' + idStore).text() + '</strong></span>');                },                'text'            );        }        else { toastr.error('Saisie abandonnée ou incorrecte.', 'Échec.'); }    }        // Rename an Enterprise password LIVE    function renamePasswordEnterprise($idEnterprise) {        var newPassword = prompt("Choisir le nouveau mot de passe (8 caractères min.) :"); // Get the previous value of file description        if(newPassword != null && newPassword.length >= 8) { // Check before update                        newPassword = newPassword.trim(); // The trim() method removes whitespace from both sides of a string            $.post(                './ajax/rename_password_enterprise.php',                {                    $idEnterprise  : $idEnterprise,                    newPassword : newPassword                },                function(data) {                    data = data.trim();                    if(data != '-1')                        $('#tdPasswordStore_' + $idEnterprise).html('<span style="color:Green!important"><strong>' + data + '</strong></span>');                        // location.reload();                    else                        $('#tdPasswordStore_' + $idEnterprise).html('<span style="color:Red!important"><strong>' + $('#tdPasswordStore_' + $idEnterprise).text() + '</strong></span>');                },                'text'            );        }        else { toastr.error('Saisie abandonnée ou incorrecte.', 'Échec.'); }    }    // Delete a Store LIVE    function deleteStore(idStore) {        if ( confirm("Confirmer votre choix ?") != false ) { // The confirm() method returns true if the user clicked "OK", and false otherwise.            $.post(                './ajax/delete_store.php',                {                    idStore : idStore                                    },                function(data) {                    if(data.trim() == '1')                        location.reload();                },                'text'            );                }    }    </script></body></html>