<?phprequire_once dirname ( __FILE__ ) . '/../domain/PurchasingFair.class.php';require_once dirname ( __FILE__ ) . '/../services/AppServiceImpl.class.php';if(!isset($_SESSION)) session_start(); // Start session$appService = AppServiceImpl::getInstance();if( isset($_POST['mailEnterprise']) && !empty($_POST['mailEnterprise']) && isset($_POST['passwordEnterprise']) && !empty($_POST['passwordEnterprise'])) {    $mailEnterpriseSent = (string) $_POST['mailEnterprise'];    $passwordEnterpriseSent = (string) $_POST['passwordEnterprise'];    $profileEnterpriseSent = (string) $_POST['profileEnterprise'];    $enterpriseConcerned = $appService->authentication($mailEnterpriseSent, $passwordEnterpriseSent, $profileEnterpriseSent);    if( $enterpriseConcerned !== NULL ) { // If successful, an Enterprise Object is inserted into the session    	$Log = $appService->createLog($enterpriseConcerned, $appService->get_ip());    	$appService->saveLog($Log);        $_SESSION['enterpriseConcerned'] = $enterpriseConcerned;        $_SESSION['isStoreOrProvider']   = ($enterpriseConcerned->getOneProfile()->getName() == 'Magasin') ? 'store' : 'provider';        echo 'Success';    } else { echo 'Failed'; } // Selection Failed}?>