nisseur selectionné        $EndTimeBis = substr($EndTime, 0,2).substr($EndTime, 3,2).'00';        for ($i=0; $i < sizeof($tab3); $i++) {            $var1 = substr($tab3[$i], 0, 8); // ex : $var1 = 08:00:00            if($var1 == $StartTime.':00'){                for($j=$i; $j < sizeof($tab3); $j++){                    $var1bis = substr($tab3[$j], 0, 8); // ex : $var1 = 08:00:00                    $var2 = substr($tab3[$j], 11, 8); // ex : $var1 = 08:30:00                    $var2bis = substr($var2, 0,2).substr($var2, 3,2).substr($var2, 6,2); //format : ex : 083000                    if($var2bis <= $EndTimeBis){                        $arrayTpm[] = $var1bis.' - '.$var2;                    }                }            }        }        return $arrayTpm;    }    public function deleteRDV($idPurchasingFair, $idStore, $idFournisseur, $start_datetime){        return $this->prise_rdv_storeDAO->delete($idPurchasingFair, $idStore, $idFournisseur, $start_datetime);    }    public function findAllEnterprisesAsStoresPfBis($searchedIdPurchasingFair) {         $arrayStoresPf = $this->prise_rdv_storeDAO->findByIdPfBis($searchedIdPurchasingFair);        return $arrayStoresPf;    }    public function findAllEnterprisesAsProviderPfBis($searchedIdPurchasingFair) {         $arrayStoresPf = $this->prise_rdv_storeDAO->findByIdPfBisProvider($searchedIdPurchasingFair);        return $arrayStoresPf;    }    /* ------------------------------------------------ ./prise_rdv_storeDAO methods ------------------------------------------------ */    /* ------------------------------------------------ UnavailabilityDAO methods ------------------------------------------------  */    public function createUnavailability($startDatetime, $endDatetime, $idEnterprise, $idPurchasingFair) { return $createdUnavailability = new Unavailability($startDatetime, $endDatetime, $idEnterprise, $idPurchasingFair); }    public function deactivateUnavai